import React from 'react'
import {Link} from 'react-router-dom'
import {connect} from 'react-redux'
import {
  getTotalBasketCount,
  getTotalBasketPrice
} from 'selectors'

const BacketCart = ({totalBasketCount, totalPrice}) => {
  return (
    <div className='cart'>
        <div className='dropdown'>
          <Link
            to='/basket'
            id='dlabel'
            className='btn btn-inverse btn-blick btn-lg'
            >
            <i className='fa fa-fa-shopping-cart'></i>
          <span>{totalBasketCount} item(s) - ${totalPrice}</span>
          </Link>
        </div>
    </div>
  )
}

const mapStateToProps = state => {
  return {
    totalBasketCount: getTotalBasketCount(state),
    totalPrice: getTotalBasketPrice(state)
  }
}

export default connect (mapStateToProps, null)  (BacketCart)